package com.example.hugh.life.commmon.util;

import javax.script.Invocable;
import javax.script.ScriptEngine;
import javax.script.ScriptEngineManager;
import javax.script.ScriptException;
import java.io.FileNotFoundException;
import java.io.FileReader;

public class JSEngineUtil {

    static void initEngine(){
        ScriptEngineManager manager = new ScriptEngineManager();
        ScriptEngine engine = manager.getEngineByName("nashorn");

        String jsFileName = "src\\main\\resources\\decodeDouBan.js";   // 读取js文件

        FileReader reader = null;   // 执行指定脚本
        try {
            reader = new FileReader(jsFileName);
            String aa = reader.getEncoding();
            System.out.println("aa:" + aa);
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        }

        try {
            engine.eval(reader);
            Invocable invocable = (Invocable) engine;
            String args= "";
            String result = (String) invocable.invokeFunction("decrypt", args);
            System.out.println(result);
        } catch (ScriptException | NoSuchMethodException e) {
            e.printStackTrace();
        }

    }

    public static void main(String[] args){

        String js = FileUtil.readFile("src\\main\\resources\\decodeDouBan.js");
        initEngine();
    }
}
